import React, { useState } from 'react';
import { useAuthContext } from '../../hooks/userAuthContext';
import Logout from '../misc/Logout';
import { useNavigate } from 'react-router-dom';

// styles
import '../../sass/style.scss'

const UserConfirmation = () => {
    const [confirmed, setConfirmed] = useState(false);
    const navigate = useNavigate();
    const [products, setProducts] = useState(null)

    const handleConfirmation = () => {
        setConfirmed(true);
    }

    const handleEscape = () => {
        navigate('/landing');
    }

    useEffect(() => {
        const fetchMenu = async () => {
            const response = await fetch('/api/products')
            const json = await response.json()

            if (response.ok) {
                setProducts(json)
            }
        }

        fetchMenu()
    }, [])

    return (

        <div className="user-confirmation__container">

            {!confirmed && (
                <div>
                    <div className="user-confirmation__container-text">
                        <h2>Vänligen bekräfta ditt val</h2>
                        <div className="user-confirmation__product">
                            <i className="fa-solid fa-utensils"></i>

                            {products && (
                                <>
                                    {products.map((product, index) => {
                                        if (product.category === 'Fika') {
                                            if (index === 0 || products[index - 1].category !== 'Fika') {
                                                return <button key={product._id}>Fika</button>;
                                            }
                                        } else {
                                            return <button onClick={handlePopup} key={product._id}>{product.category}</button>;
                                        }
                                        return null;
                                    })}
                                </>
                            )}

                        </div>
                    </div>
                    <div className="user-confirmation__buttons">
                        <button className="user-confirmation__buttons-escape" onClick={handleEscape}>Avbryt</button>
                        <button className="user-confirmation__buttons-ok" onClick={handleConfirmation}><p>Ok</p></button>
                    </div>
                </div>
            )}

            {confirmed && (
                <div>
                    <div className="user-confirmation__container-text">
                        <h2>Tack för din beställning</h2>
                        <div className="user-confirmation__product">Du loggas automatiskt ut om: 00</div>
                    </div>
                    <div className="user-confirmation__buttons">
                        <Logout />
                    </div>
                </div>
            )}

        </div>
    )
}

export default UserConfirmation
