import React from 'react';
import { useEffect, useState } from "react"



// <div className="users">
// {products && products.map((product) => (
// product.menu && product.menu.map((item) => (
//     <p key={item._id}>{item.type}</p>
//     ))
// ))}
// </div>
// styles
import './categories.css'

const Categories = () => {

    const [products, setProducts] = useState(null)
    const [showSubProducts, setShowSubProducts] = useState(false);

    const handleClick = () => {
        setShowSubProducts(true);
    }

    useEffect(() => {
        const fetchMenu = async () => {
            const response = await fetch('/api/products')
            const json = await response.json()

            if (response.ok) {
                setProducts(json)
            }
        }

        fetchMenu()
    }, [])

    return (

        <div className="categories__container">
            <div className="categories__container-products">
                <ul>
                    {products && (
                        <>
                            {products.map((product, index) => {
                                if (product.category === 'Fika') {
                                    if (index === 0 || products[index - 1].category !== 'Fika') {
                                        return <li><button onClick={handleClick} key={product._id}>Fika</button></li>;
                                    }
                                } else {
                                    return <li><button key={product._id}>{product.category}</button></li>;
                                }
                                return null;
                            })}
                        </>
                    )}
                </ul>
            </div>
        </div>
    );

}

export default Categories
