import React, { useState } from 'react';

// styles

const UserConfirmation = () => {
    const [popupInfo, setPopupInfo] = useState({title: "Order Confirmation", message: "Are you sure you want to confirm your order?"});
    const [btnInfo, setBtnInfo] = useState({text: "Ok"});

    const handleConfirmation = () => {
        setPopupInfo({title: "Order confirmed", message: "Your order has been confirmed. Thank you for your purchase bitch!"});
        setBtnInfo({text: "Ok"});
    }

    return (
        <div className="user-confirmation__container">
            <div className="user-confirmation__container-text">
                <h2 className="user-confirmation__heading">{popupInfo.title}</h2>
                <p>{popupInfo.message}</p>
                <div className="user-confirmation__product">Icon and choosen product</div>
            </div>
            <div className="user-confirmation__buttons">
                <button>Avbryt</button>
                <button onClick={handleConfirmation}>{btnInfo.text}</button>
            </div>
        </div>
    )
}

export default UserConfirmation
