import { useState } from 'react'
import { useHistory } from 'react-router'; // Import useHistory hook
import { useLogin } from '../../hooks/useLogin'

const UserStart = () => {
  const [users, setUsers] = useState ('')
  const [ssn, setSsn] = useState ('')
  const [password, setPassword] = useState ('')
  const {login, error, isLoading} = useLogin()

  const history = useHistory(); // Instantiate useHistory hook

  const handleSubmit = async (e) => {
    e.preventDefault()

    //console.log(users, password);
    const loginSuccessful = await login(ssn, password); // Get response from login attempt

    if (loginSuccessful) { // If login is successful
      history.push('/userlanding'); // Navigate to new page
    }
  }

  return (
    <form className="login" onSubmit={handleSubmit}>
      <h3>Logga in</h3>

      <label>Personnummer:</label>
      <input type="text" 
            name="ssn"
            onChange={(e) => setSsn(e.target.value)}
            value={ssn}
      />
      <label>PIN</label>
      <input type="password" 
            name="password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
      />

      <button onClick={handleSubmit}>Logga in</button>
      { error && <div className="error">{error}</div>}
    </form>
  )
}

export default UserStart


