import { useState } from 'react';
import { useLogin } from '../../hooks/useLogin';
import { useAuthContext } from '../../hooks/userAuthContext';
import { withRouter } from 'react-router-dom'; // import withRouter HOC

const UserStart = (props) => {
  const { user } = useAuthContext();
  const [ssn, setSsn] = useState('');
  const [password, setPassword] = useState('');
  const { login, error, isLoading } = useLogin();

  const handleSubmit = async (e) => {
    e.preventDefault();

    await login(ssn, password);

    if (!error) {
      props.history.push('/newpage');
    }
  };

  return (
    <div className="container">
      {user && (
        <div>
          <h2>{user.name}</h2>
        </div>
      )}

      <form className="login" onSubmit={handleSubmit}>
        <h3>Logga in</h3>

        <label>Personnummer:</label>
        <input
          type="text"
          name="ssn"
          onChange={(e) => setSsn(e.target.value)}
          value={ssn}
        />

        <label>PIN</label>
        <input
          type="password"
          name="password"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
        />

        <button>Logga in</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default withRouter(UserStart); // wrap your component with withRouter HOC



